# include "Class_VolTri.hpp"

// ========================================================================== //
// IMPLEMENTATIONS                                                            //
// ========================================================================== //

// dgf format =============================================================== //

// -------------------------------------------------------------------------- //
void Class_VolTri::Import_dgf(
    string          file_name
) {

// ========================================================================== //
// void Class_VolTri::Import_dgf(                                             //
//     string          file_name)                                             //
//                                                                            //
// Import volume triangulation from .dgf file.                                //
// ========================================================================== //
// INPUT                                                                      //
// ========================================================================== //
// - file_name      : string, dgf file name                                   //
// ========================================================================== //
// OUTPUT                                                                     //
// ========================================================================== //
// - none                                                                     //
// ========================================================================== //

// Local variables
int                  n = 0;
bitpit::DGFObj              DGF(bitpit::utils::trim(file_name));

// Counters
// none

// ========================================================================== //
// READ MESH DATA                                                             //
// ========================================================================== //
DGF.load(nVertex, nSimplex, Vertex, Simplex);

// ========================================================================== //
// UPDATE INTERNAL FLAG                                                       //
// ========================================================================== //
DGF.loadSData("types", n, e_type);

return; };

// -------------------------------------------------------------------------- //
void Class_VolTri::Export_dgf(
    string          file_name
) {

// ========================================================================== //
// void Class_VolTri::Export_dgf(                                             //
//     string          file_name)                                             //
//                                                                            //
// Export volume triangulation in .dgf file.                                  //
// ========================================================================== //
// INPUT                                                                      //
// ========================================================================== //
// - file_name      : string, dgf file name                                   //
// ========================================================================== //
// OUTPUT                                                                     //
// ========================================================================== //
// - none                                                                     //
// ========================================================================== //

// Local variables
bitpit::DGFObj              DGF(bitpit::utils::trim(file_name));

// Counters
// none

// ========================================================================== //
// READ MESH DATA                                                             //
// ========================================================================== //
DGF.save(nVertex, nSimplex, Vertex, Simplex);

// ========================================================================== //
// UPDATE INTERNAL FLAG                                                       //
// ========================================================================== //
DGF.appendSData("types", nSimplex, e_type);

return; };
